function activeScriptNode(scriptNode){if(scriptNode.getAttribute("type")==="text/plain"){var cloneScriptNode=scriptNode.cloneNode(true);cloneScriptNode.setAttribute("type","text/javascript");scriptNode.parentNode.replaceChild(cloneScriptNode,scriptNode)}}
function inactiveScriptNode(scriptNode){if(scriptNode.getAttribute("type")==="text/javascript"){var cloneScriptNode=scriptNode.cloneNode(true);cloneScriptNode.setAttribute("type","text/plain");scriptNode.parentNode.replaceChild(cloneScriptNode,scriptNode)}}function removeCookie(cookie){var expirationTime=new Date;expirationTime.setDate(expirationTime.getDate()-1);document.cookie=cookie+"\x3d"+escape("")+";expires\x3d"+expirationTime.toUTCString()+";"}
function activeCookieScripts(cookieClassName){var selectorName="."+cookieClassName;$(selectorName).each(function(){activeScriptNode($(this)[0])})}
$(function(){function enableCorrectCookie(){if(!enableNewCookieCfg)activeCookieScripts("cookie-package-controller");else{if(ctlAnalysisCookie)activeCookieScripts("analysis-cookie");if(ctlAdvertiseCookie)activeCookieScripts("advertise-cookie")}}var isAccept=getCookie("huawei_store_accept_cookie_flag");if(isAccept==="true")enableCorrectCookie();var enableControl=$(".cookie-data").length>0?$(".cookie-data").attr("the-package-control-of-cookies-enabled"):"";$(".convergent-cookie-agree").on("click",function(){enableCorrectCookie()});
$(".convergent-cookie-close").on("click",function(){if($(".convergent-cookie-agree").length>0)if(!enableNewCookieCfg&&enableControl||enableNewCookieCfg&&(ctlAnalysisCookie||ctlAdvertiseCookie)){setJPCookie("huawei_store_accept_cookie_flag",true,360);setJPCookie("huawei_store_accept_cookie_choose","1|0|0",360)}else{setJPCookie("huawei_store_accept_cookie_flag",false,360);setJPCookie("huawei_store_accept_cookie_choose","0|0|0",360)}})});