$(document).ready(function(){$(".huawei-cbg-pc-mobile-redirect").each(function(){var destinationUrl=$(this).data("destinationurl")+location.search;var isMobileRedirect=$(this).data("ismobile")!=undefined&&$(this).data("ismobile")==true;var isMobile=Mkt.Util.isMobileEnd();if(isMobile&&!isMobileRedirect||!isMobile&&isMobileRedirect)if(destinationUrl.length>0){window.location.href=destinationUrl;return false}})});